import {useState,useEffect} from 'react';
import './App.css';
import Login from './components/Login'
function App() {
  let [isLogin,SetisLogin] = useState(false);
  let [session,setSession] = useState(null);

  const handleLogin =(event)=>{
    event.preventDefault()
    let {uid,pass}=document.forms[0]
    console.log("user id",uid.value)
    SetisLogin(true)
  }


  return (
    <div className="App">
      {!isLogin &&<>
      <h1>Welcome to MeriBasket</h1>
      <form onSubmit={handleLogin}>
        <div>
        <label>UserID</label>
        <br></br>
        <input type="text" name="uid" required placeholder='Enter User ID'></input>
        </div>
        <div>
        <label>Password</label>
        <br></br>
        <input type="password" name="pass" required placeholder='Enter password'></input>
        </div>
        <div> 
        <button type="submit">Login</button>
        </div>
      </form>
      </>
    }
    {isLogin && <>
    <h1>Home Page</h1>
    <button type="submit" onSubmit={()=>SetisLogin(false)}>Logout</button>
    </>
    }
    </div>
  );
}

export default App;
