import logo from './logo.svg';
import {useState,useEffect} from 'react';
import './App.css';
import Login from './components/Login'
import Homepage from './components/Homepage'
function App() {
  const [isLogin,setLogin] = useState(sessionStorage.getItem("isLoggedin") === true);
  useEffect(() => {
    setLogin(sessionStorage.getItem("isLoggedin") === true)
  },[sessionStorage.getItem("isLoggedin")])
  console.log("log ",isLogin)
  return (
    <div className="App">
      {isLogin ?
      <Login />
      :
      <div>
        <button>Profile</button>
        <button>Cart</button>
        <button onClick={()=>sessionStorage.setItem("isLoggedin",false)}> Logout </button>
      <Homepage />
      </div>
    }
    </div>
  );
}

export default App;
