import logo from './logo.svg';
import {useState,useEffect} from 'react';
import './App.css';
import Login from './components/Login'
import Homepage from './components/Homepage'
function App() {
  const [isLogin,setLogin] = useState(false);

  const handleLogin = () => {
    let {uid,pass} = document.forms[0]
    Login({uid: uid.value, })
  }

  const handleLogout = (event) => {

    setLogin(false)
    console.log("log ",isLogin)
  }

  return (
    <div className="App">
      {!isLogin ?
      <>
      <h1>Welcome to MeriBasket</h1>
      <form onSubmit={handleLogin}>
        <div>
        <label>UserID</label>
        <br></br>
        <input type="text" name="uid" required placeholder='Enter User ID'></input>
        </div>
        <div>
        <label>Password</label>
        <br></br>
        <input type="password" name="pass" required placeholder='Enter password'></input>
        </div>
        <div> 
        <button type="submit">Login</button>
        </div>
      </form>
      </>
      :
      <div>
        <button>Profile</button>
        <button>Cart</button>
        <button onClick={handleLogout}> Logout </button>
      <Homepage />
      </div>
    }
    </div>
  );
}

export default App;
