const Login = () => {
    let {uid,pass}=document.forms[0].value

    console.log("uid ",uid)
    const options = {
        method : 'POST',
        headers : {
            'Content-Type' : 'application/json'
        },
        body : JSON.stringify({uid,pass})
    }
    
    fetch('http://127.0.0.1:5000/api/login',options)  // API endpoint of your Flask server
        .then(response => response.json())
        .then(data => {
        console.log("data ",data)
        })
        .catch(error => {
        console.error('Error:', error);
    });

    return(
        <>
      <h1>Welcome to MeriBasket</h1>
      <form onSubmit={Login}>
        <div>
        <label>UserID</label>
        <br></br>
        <input type="text" name="uid" required placeholder='Enter User ID'></input>
        </div>
        <div>
        <label>Password</label>
        <br></br>
        <input type="password" name="pass" required placeholder='Enter password'></input>
        </div>
        <div> 
        <button type="submit">Login</button>
        </div>
      </form>
      </>
    )

}
