import logo from './logo.svg';
import {useState,useEffect} from 'react';
import './App.css';
import Login from './components/Login'
import Homepage from './components/Homepage'
function App() {
  let [isLogin,setLogin] = useState(false);
  let [user,setUser] = useState();

  useEffect(() => {
    setLogin(isLogin)
    console.log("Effect ",isLogin)
  },[isLogin])

  const handleLogin = (event) => {
    event.preventDefault()
    let {uid,pass} = document.forms[0]
    Login({uid: uid.value, pass: pass.value})
    if(sessionStorage.getItem("user")){
    setLogin(true)
    }
  }

  const handleLogout = (event) => {  
    event.preventDefault()
    setLogin(false)
  }

  return (
    <div className="App">
      {isLogin ?
      <>
        <button>Profile</button>
        <button>Cart</button>
        <button onClick={handleLogout}> Logout </button>
      <Homepage />
      </>
      :
      <>
      <h1>Welcome to MeriBasket</h1>
      <form onSubmit={handleLogin}>
        <div>
        <label>UserID</label>
        <br></br>
        <input type="text" name="uid" required placeholder='Enter User ID'></input>
        </div>
        <div>
        <label>Password</label>
        <br></br>
        <input type="password" name="pass" required placeholder='Enter password'></input>
        </div>
        <div> 
        <button type="submit">Login</button>
        </div>
      </form>
      </>
      }

    </div>

  )
}

export default App;
