import logo from './logo.svg';
import {useState,useEffect} from 'react';
import './App.css';
import Login from './components/Login'
import Homepage from './components/Homepage'
function App() {
  const [isLogin,setLogin] = useState(false);
  const [user,setUser] = useState(null)


  const handleLogin = (event) => {
    event.preventDefault()
    let {uid,pass} = document.forms[0]

    setUser(Login({uid: uid.value, pass: pass.value}))
    console.log("user ",use)
    if(user!=null){
      setLogin(true)
    }

    console.log("login ",isLogin)
  }

  const handleLogout = (event) => {

    sessionStorage.setItem("isLoggedin",false)
    setLogin(false)
    console.log("login ",isLogin)
  }

  return (
    <div className="App">
      {(isLogin==false) ?
      <>
      <h1>Welcome to MeriBasket</h1>
      <form onSubmit={handleLogin}>
        <div>
        <label>UserID</label>
        <br></br>
        <input type="text" name="uid" required placeholder='Enter User ID'></input>
        </div>
        <div>
        <label>Password</label>
        <br></br>
        <input type="password" name="pass" required placeholder='Enter password'></input>
        </div>
        <div> 
        <button type="submit">Login</button>
        </div>
      </form>
      </>
      :
      <>
        <button>Profile</button>
        <button>Cart</button>
        <button onClick={handleLogout}> Logout </button>
      <Homepage />
      </>
    }
    </div>
  );
}

export default App;
