import { useEffect } from "react"

export default function login(props){
    let uid=props.userid
    let pass=props.password
    const options = {
        method : 'POST',
        headers : {
            'Content-Type' : '/application/json'
        },
        body : JSON.stringify({uid,pass})
    }
    
        fetch('http://127.0.0.1:5000/api/login',options)  // API endpoint of your Flask server
          .then(response => response.json())
          .then(data => {
            data.map(item=>(
              console.log(item)
            ))
          })
          .catch(error => {
            console.error('Error:', error);
          });
      

}
