import { useEffect,useState } from "react"
import {BrowserRouter as Router,Routes, Route, NavLink} from 'react-router-dom';

export default function Orders () {

    const [detail,setDetail] = useState([]);
    useEffect(()=>{
        const userDetail = sessionStorage.getItem("user")
        console.log("details ",userDetail)
        const options = {
            method : 'POST',
            headers : {
                'Content-Type' : 'application/json'
            },
            body : JSON.stringify({userDetail})
        }

        fetch('http://127.0.0.1:5000/api/Orders',options)  // API endpoint of your Flask server
        .then(response => response.json())
        .then(data => {
        console.log("data ",data)
        setDetail(data)
        })
        .catch(error => {
        console.error('Error:', error);
    });
    },[]);

    const handleOrder = () => {
        console.log("placing...")

    }

    return (
        <div>
            <>
                <Router>
                
                <Routes>
                <Route path="/Profile" element={<Profile />} />
                <Route path="/" element={<Homepage />} />
                <Route path="/Cart" element={<Cart />} />
                <Route path="/Orders" element={<Orders />} />
                </Routes>
                </Router>
            </>
            Your Orders
            {detail.length? <>{detail.map((items,index) => (
                <div key={index}>{items}
                {items[1]? <button onClick={handleOrder}>Place Order</button>:<></> }
                </div>
            ))}</> : <>No orders</>}
        </div>
    )
}
