import { useState } from "react"
export default function Homepage(){
    const [results,setResults] = useState(false)
    const[products,setProducts] = useState()

    const handleLogout = () => {
        console.log("b ",sessionStorage.getItem("isLoggedin"))
        sessionStorage.setItem("isLoggedin",false)
    }
    const fetchProducts = (props) =>{
        let search_query = props.query
        console.log("fetching products...")
        const options = {
            method : 'POST',
            headers : {
                'Content-Type' : 'application/json'
            },
            body : JSON.stringify({search_query})
        }
        
        fetch('http://127.0.0.1:5000/api/Search',options)  // API endpoint of your Flask server
            .then(response => response.json())
            .then(data => {
                setResults(true)
                setProducts(data)
                console.log("data ",products)
                
            })
            .catch(error => {
            console.error('Error:', error);
        });
    }

    const home = (event) =>{
        event.preventDefault()
        let {query} = document.forms[0]
        query=query.value
        console.log(query)
        fetchProducts({query: query})

    }
    return (
    <div>
        <>
      <h1>Welcome to MeriBasket</h1>
      <form onSubmit={handleLogin}>
        <div>
        <label>UserID</label>
        <br></br>
        <input type="text" name="uid" required placeholder='Enter User ID'></input>
        </div>
        <div>
        <label>Password</label>
        <br></br>
        <input type="password" name="pass" required placeholder='Enter password'></input>
        </div>
        <div> 
        <button type="submit">Login</button>
        </div>
      </form>
      </>
    <>
      <h1>Home Page</h1>
      <form onSubmit={home}>
      <input name="query" type="text" placeholder='Search any grocery'></input>
      <button type="submit"> Search</button>
      </form>
      </>
    {results &&
    <>
        <h1>products</h1>
        {products}
        </>
    }
    </div>
    )
}
