import logo from './logo.svg';
import {useState,useEffect} from 'react';
import {BrowserRouter as Router,Routes, Route, NavLink,useNavigate, Navigate} from 'react-router-dom';
import './App.css';
import Login from './components/Customer/Login'
import Homepage from './components/Customer/Homepage'
import Profile from './components/Customer/Profile'
import Cart from './components/Customer/Cart'
import Orders from './components/Customer/Orders'
import Checkout from './components/Customer/Checkout'
import  Home from './components/Retail/Home';
import  ManageOrder from './components/Retail/ManageOrder';
import  ManageDiscount from './components/Retail/ManageDiscount';

function App() {
  const [isLogin,setLogin] = useState(true);
  const [user,setUser] = useState(null);
  const [LoginType,setLoginType] = useState('');
  const [path,setPath] = useState('');


  useEffect(() => {
    setLogin(!isLogin)
  },[user])

  const navigate = useNavigate()

  const handleLogin = (event) => {
    event.preventDefault()
    let {uid,pass} = document.forms[0]
    Login({uid: uid.value, pass: pass.value})
    setUser(sessionStorage.getItem("user"))
    if(sessionStorage.getItem("user")!=null){

      if(LoginType === 'Customer'){
        setPath("/Customer/Home")
      }
      else{

        setPath("/Retail/Home")
      }
    }
  }

  const handleLogout = (event) => {  
    event.preventDefault()
    sessionStorage.removeItem("user")
    setUser(null)
  }

  const handleLoginType = (ele) => {
    setLoginType(ele)
  }

  const handlePath = (ele) => {
    setPath(ele)
  }
  console.log("PATH ",path)

  return (
    <div className="App">
      {isLogin ?
      <>
        {LoginType === 'Customer' ? 
        <>
        <NavLink onClick={handleLogout}> Logout </NavLink>
        <NavLink onClick={() => handlePath("/Customer/Profile")}>Profile</NavLink>
        <NavLink onClick={() => handlePath("/Customer/Home")}>Home</NavLink>
        <NavLink onClick={() => handlePath("/Customer/Cart")}>Cart</NavLink>
        <NavLink onClick={() => handlePath("/Customer/Orders")}>Orders</NavLink>
        <Navigate to={path} />

        </>
        :
        <>
          <NavLink onClick={handleLogout}> Logout </NavLink>
          <NavLink onClick={() => handlePath("/Retail/Home")}>Home</NavLink>
          <NavLink onClick={() => handlePath("/Retail/ManageOrder")}>Manage Orders</NavLink>
          <NavLink onClick={() => handlePath("/Retail/ManageDiscount")}>Manage Discounts</NavLink>
          <Navigate to={path} /> 

        </>
        }
        <Routes>
          <Route path="/Customer/Profile" element={<Profile />} />
          <Route path="/Customer/Home" element={<Homepage />} />
          <Route path="/Customer/Cart" element={<Cart />} />
          <Route path="/Customer/Orders" element={<Orders />} />
          <Route path="/Customer/Checkout" element={<Checkout/>}/>
          <Route path="/Retail/Home" element={<Home />}/>
          <Route path="/Retail/ManageOrder" element={<ManageOrder />}/>
          <Route path="/Retail/ManageDisount" element={<ManageDiscount />}/>

        </Routes>
      </>
      :
      <>
      <h1>Welcome to MeriBasket</h1>

      <p>Login As {LoginType}</p>
      <input type='radio' value="Customer" checked={LoginType === 'Customer'} onChange={() => handleLoginType("Customer")}/><label>Customer</label>
      <input type='radio' value="Retail" checked={LoginType === 'Retail'} onChange={() => handleLoginType("Retail")}/><label>Retail Store</label>
      <form onSubmit={handleLogin}>
      <br></br>
        <div>
        <label>UserID</label>
        <br></br>
        <input type="text" name="uid" required placeholder='Enter User ID'></input>
        </div>
        <div>
        <label>Password</label>
        <br></br>
        <input type="password" name="pass" required placeholder='Enter password'></input>
        </div>
        <div> 
        <button type="submit">Login</button>
        </div>
      </form>
      </>
      }
    </div>

  )
}

export default App;
