import logo from './logo.svg';
import {useState,useEffect} from 'react';
import {Routes, Route} from 'react-router-dom';
import './App.css';
import Login from './components/Login'
import Homepage from './components/Homepage'
import Profile from './components/Profile'
function App() {
  let [isLogin,setLogin] = useState(false);
  let [user,setUser] = useState();

  useEffect(() => {
    setLogin(!isLogin)
  },[user])

  const handleLogin = (event) => {
    event.preventDefault()
    let {uid,pass} = document.forms[0]
    Login({uid: uid.value, pass: pass.value})
    setUser(sessionStorage.getItem("user"))
    if(user){
      console.log("yes")
    }
  }

  const handleLogout = (event) => {  
    event.preventDefault()
    sessionStorage.removeItem("user")
    setUser(null)
  }


  return (
    <div className="App">
    <Routes>
      <Route exact path="/Profile" Component={<></>} />
    </Routes>
      {isLogin ?
      <>


        <button onClick={handleLogout}> Logout </button>
      <Homepage />
      </>
      :
      <>
      <h1>Welcome to MeriBasket</h1>
      <form onSubmit={handleLogin}>
        <div>
        <label>UserID</label>
        <br></br>
        <input type="text" name="uid" required placeholder='Enter User ID'></input>
        </div>
        <div>
        <label>Password</label>
        <br></br>
        <input type="password" name="pass" required placeholder='Enter password'></input>
        </div>
        <div> 
        <button type="submit">Login</button>
        </div>
      </form>
      </>
      }
    </div>

  )
}

export default App;
