import logo from './logo.svg';
import {useState,useEffect} from 'react';
import './App.css';
import Login from './components/Login'
function App() {
  cosnt [isLogin,SetisLogin] = useState();
  let [session,setSession] = useState(null);

  const handleLogin = (event)=>{
    event.preventDefault()
    let {uid,pass}=document.forms[0]
    console.log("user id",uid.value)
    Login({userid: uid.value,password: pass.value})
    SetisLogin(true)
  }

  const handleLogout = (event)=>{
    SetisLogin(false)
  }

  const handleSearch = (event) =>{
    event.preventDefault()
    let {query} = document.forms[0]
    console.log("query ",query.value)

  }


  return (
    <div className="App">
      {!isLogin &&<>
      <h1>Welcome to MeriBasket</h1>
      <form onSubmit={handleLogin}>
        <div>
        <label>UserID</label>
        <br></br>
        <input type="text" name="uid" required placeholder='Enter User ID'></input>
        </div>
        <div>
        <label>Password</label>
        <br></br>
        <input type="password" name="pass" required placeholder='Enter password'></input>
        </div>
        <div> 
        <button type="submit">Login</button>
        </div>
      </form>
      </>
    }
    {isLogin && 
      <>
      <h1>Home Page</h1>
      <button onClick={handleLogout}>Logout</button>
      <form onSubmit={{handleSearch}}>
      <input type="text" placeholder='Search any grocery'></input>
      <button type="submit"> Search</button>
      </form>
      </>
    }
    </div>
  );
}

export default App;
